import { Component } from '@angular/core';
import { MensajesComponent } from '../mensajes/mensajes.component';
import { Mensajes } from '../mock-mensaje';

@Component({
  selector: 'app-favoritas',
  templateUrl: './favoritas.component.html',
  styleUrls: ['./favoritas.component.css']
})
export class FavoritasComponent {
  public bd:any
  public lista:any
  public _mensaje: MensajesComponent
  public mensajes = Mensajes
  constructor(){
    this._mensaje = new MensajesComponent
    this.mensajes = Mensajes
  }

  listarFavs(){
    const peticion =window.indexedDB.open("PacoApp")
				peticion.onsuccess= ((evento:any) =>{
					this.bd=evento.target.result;	
					console.log('Base de datos cargada.')
					const objectStore =this.bd.transaction('Favoritas', 'readonly').objectStore('Favoritas')
					const peticion = objectStore.getAll()
					peticion.onsuccess= (() =>{
						this.lista= peticion.result
						console.log(this.lista)
					}).bind(this)
          peticion.onerror= (() =>{
						this.lista=42;
					}).bind(this)
				}).bind(this)
  }
}
<h1>Favoritas</h1>
<button (click)="listarFavs()">Listar</button>
<div *ngIf="lista!=42" class="{{mensajes[3].tipo}}">
    <h2>{{mensajes[3].titulo}}</h2>
    <p>{{mensajes[3].description}}</p>
</div>
<div *ngIf="lista==42" class="{{mensajes[4].tipo}}">
    <h2>{{mensajes[4].titulo}}</h2>
    <p>{{mensajes[4].description}}</p>
</div>
<table>
    <thead>
        <tr>
          <th>Imagen</th>
          <th>Nombre</th>
        </tr>
      </thead>
      <tbody>
        <tr *ngFor="let listas of lista">
          <td><img src={{listas.image}} alt={{listas.title}} width="128px" height="128px"></td>
          <td>{{listas.title}}</td>
        </tr>
      </tbody>
</table>
